mapper.select:
  select p.id, prjtype, local, prjname, d.simplename place, area, begintime, endtime, provider, jsfa, dljd,
  dlwd, d.tips qjpic1, qjpic2, qjpic3, createtime, updatetime, isdelete,deptid,deptsubid,sfxj
  from s_prj_base p left join dept d on p.deptsubid=d.id
where 1=1

  and prjtype = #{prjtype,jdbcType=VARCHAR}


  and deptid = #{deptid,jdbcType=VARCHAR}


  and deptsubid = #{deptsubid,jdbcType=VARCHAR}


  and local = #{local,jdbcType=VARCHAR}

and isdelete=0
