mapper.select:
SELECT d.id "id", d.pId "pId", d.fullname AS "name", ( CASE WHEN (pId = 0 OR pId IS NULL) THEN 'true' ELSE 'false' END ) "open", ( CASE WHEN (d1.ID = 0 OR d1.ID IS NULL) THEN 'false' ELSE 'true' END ) "checked" FROM dept d LEFT JOIN ( SELECT ID FROM dept WHERE ID IN
#{i}
) d1 ON d.ID = d1.ID ORDER BY pId, num ASC

select id,pid,simplename,fullname,tips,version from dept dept where 1=1
and id=#{deptid}
and pid = #{pid}
and (simplename like CONCAT('%',#{deptName},'%') or fullname like CONCAT('%',#{deptName},'%'))
order by id desc
